import React, { useState, useRef, useEffect } from 'react';
import Pizzicato from 'pizzicato';
import './App.css'; // Your custom styles

function App() {
  const [isRecording, setIsRecording] = useState(false);
  const audioContext = useRef(null);
  const analyser = useRef(null);
  const audioInput = useRef(null);
  const animationId = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    if (!audioContext.current) {
      audioContext.current = new (window.AudioContext || window.webkitAudioContext)();
      analyser.current = audioContext.current.createAnalyser();
      analyser.current.fftSize = 2048;
    }
  }, []);

  const startRecording = async () => {
    try {
      setIsRecording(true);
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      audioInput.current = audioContext.current.createMediaStreamSource(stream);
      audioInput.current.connect(analyser.current);
      visualizeWaveform();
    } catch (error) {
      console.error('Error accessing microphone:', error);
    }
  };

  const stopRecording = () => {
    setIsRecording(false);
    if (audioInput.current) {
      audioInput.current.disconnect();
      audioInput.current = null;
    }
    cancelAnimationFrame(animationId.current);
  };

  const visualizeWaveform = () => {
    const canvas = canvasRef.current;
    const canvasContext = canvas.getContext('2d');
    const bufferLength = analyser.current.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const draw = () => {
      analyser.current.getByteTimeDomainData(dataArray);
      canvasContext.fillStyle = '#f4f4f4';
      canvasContext.fillRect(0, 0, canvas.width, canvas.height);
      canvasContext.lineWidth = 2;
      canvasContext.strokeStyle = '#3f51b5';
      canvasContext.beginPath();

      const sliceWidth = (canvas.width * 1.0) / bufferLength;
      let x = 0;

      for (let i = 0; i < bufferLength; i++) {
        const v = dataArray[i] / 128.0;
        const y = (v * canvas.height) / 2;
        if (i === 0) {
          canvasContext.moveTo(x, y);
        } else {
          canvasContext.lineTo(x, y);
        }
        x += sliceWidth;
      }
      canvasContext.lineTo(canvas.width, canvas.height / 2);
      canvasContext.stroke();
      animationId.current = requestAnimationFrame(draw);
    };
    draw();
  };

  return (
    <div className="container">
      <h1>Live Audio Waveform</h1>
      <div className="controls">
        <button onClick={startRecording} disabled={isRecording}>
          Start Recording
        </button>
        <button onClick={stopRecording} disabled={!isRecording}>
          Stop Recording
        </button>
      </div>
      <canvas ref={canvasRef} width={600} height={200} className="waveform"></canvas>
    </div>
  );
}

export default App;
