import React, { useState, useRef, useEffect } from 'react';
import WaveSurfer from 'wavesurfer.js';
import axios from 'axios';
import MicIcon from '@mui/icons-material/Mic';
import StopIcon from '@mui/icons-material/Stop';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import SaveIcon from '@mui/icons-material/Save';
import AudioMotionAnalyzer from 'audiomotion-analyzer';
import '@mui/material/styles'; 
import './App.css'; 

function App() {
  const [isRecording, setIsRecording] = useState(false);
  const [audioURL, setAudioURL] = useState('');
  const [transcription, setTranscription] = useState('');
  const [summary, setSummary] = useState('');
  const [showWaveform, setShowWaveform] = useState(false);
  const waveformRef = useRef(null);
  const mediaRecorder = useRef(null);
  const audioChunks = useRef([]);
  const recognitionRef = useRef(null);
  const audioMotionAnalyzerRef = useRef(null);
  const audioContextRef = useRef(null);

  useEffect(() => {
    if (showWaveform && audioURL) {
      if (!waveformRef.current) {
        waveformRef.current = WaveSurfer.create({
          container: '#waveform',
          waveColor: '#3f51b5',
          progressColor: '#303f9f',
          cursorWidth: 2,
          cursorColor: '#000',
          height: 60,
          responsive: true,
          hideScrollbar: true,
        });
      }
      try {
        waveformRef.current.load(audioURL);
      } catch (error) {
        console.error('Error loading audio:', error);
      }
    }

    return () => {
      if (waveformRef.current) {
        waveformRef.current.destroy();
        waveformRef.current = null;
      }
    };
  }, [showWaveform, audioURL]);

  const startRecording = async () => {
    setIsRecording(true);
    setShowWaveform(false);
    audioChunks.current = [];

    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    mediaRecorder.current = new MediaRecorder(stream);
    mediaRecorder.current.start();

    mediaRecorder.current.ondataavailable = (event) => {
      audioChunks.current.push(event.data);
    };

    recognitionRef.current = new window.webkitSpeechRecognition();
    recognitionRef.current.lang = 'en-US';
    recognitionRef.current.interimResults = true;
    recognitionRef.current.continuous = true;

    recognitionRef.current.onresult = (event) => {
      let transcript = '';
      for (let i = event.resultIndex; i < event.results.length; i++) {
        transcript += event.results[i][0].transcript;
      }

      setTranscription((prev) => prev + ' ' + transcript); // Update transcription in real-time
    };

    recognitionRef.current.onend = () => {
      // Ensure recognition stops gracefully
      recognitionRef.current.stop();
    };

    recognitionRef.current.start();

    if (!audioContextRef.current) {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
    }
    const source = audioContextRef.current.createMediaStreamSource(stream);

    audioMotionAnalyzerRef.current = new AudioMotionAnalyzer(document.getElementById('visualizer'), {
      source: source,
      height: 200,
      ansiBands: false,
      showScaleX: false,
      bgAlpha: 0,
      overlay: true,
      smoothing: 0.7,
      mode: 0,
      channelLayout: "single",
      frequencyScale: "bark",
      gradient: "prism",
      linearAmplitude: true,
      linearBoost: 1.8,
      mirror: 0,
      radial: false,
      reflexAlpha: 0.25,
      reflexBright: 1,
      reflexFit: true,
      reflexRatio: 0.3,
      showPeaks: true,
      weightingFilter: "D"
    });
  };

  const stopRecording = () => {
    setIsRecording(false);
    mediaRecorder.current.stop();
    recognitionRef.current.stop();

    mediaRecorder.current.onstop = () => {
      const audioBlob = new Blob(audioChunks.current, { type: 'audio/wav' });
      const audioUrl = URL.createObjectURL(audioBlob);
      setAudioURL(audioUrl);
      setShowWaveform(true);
    };

    if (audioContextRef.current) {
      audioContextRef.current.close();
      audioContextRef.current = null;
    }
  };

  const playAudio = () => {
    if (waveformRef.current) {
      waveformRef.current.playPause();
    }
  };

  const saveAudio = () => {
    const a = document.createElement('a');
    a.style.display = 'none';
    a.href = audioURL;
    a.download = 'recording.wav';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const summarizeTranscript = async () => {
    if (!transcription) return;

    try {
      const response = await axios.post(
        'https://api-inference.huggingface.co/models/facebook/bart-large-cnn',
        { inputs: transcription },
        {
          headers: {
            Authorization: `Bearer ${process.env.REACT_APP_HUGGINGFACE_API_KEY}`,
          },
        }
      );

      // Convert summary into bullet points
      const bulletSummary = response.data[0].summary_text.split('. ').map(item => `â€¢ ${item}`).join('\n');
      setSummary(bulletSummary);
    } catch (error) {
      console.error('Error generating summary:', error);
    }
  };

  return (
    <div className="container">
      <h1>Lee Summary</h1>
      <div className="controls">
        <button className="material-button" onClick={startRecording} disabled={isRecording}>
          <MicIcon /> Start Recording
        </button>
        <button className="material-button" onClick={stopRecording} disabled={!isRecording}>
          <StopIcon /> Stop Recording
        </button>
      </div>

      <div id="visualizer"></div>

      {showWaveform && (
        <div className="waveform-container">
          <div id="waveform" className="waveform"></div>
        </div>
      )}
      <div className="control-buttons">
        <button className="material-button" onClick={playAudio} disabled={!audioURL}>
          <PlayArrowIcon /> Play / Pause
        </button>
        <button className="material-button" onClick={saveAudio} disabled={!audioURL}>
          <SaveIcon /> Save
        </button>
        <button className="material-button" onClick={summarizeTranscript} disabled={!transcription}>
          Summarize Transcript
        </button>
      </div>
      <div className="caption-bar" style={{ height: '150px', overflowY: 'auto', border: '1px solid #ccc', padding: '10px' }}>
        <h3>Live Transcription:</h3>
        <p>{transcription}</p>
      </div>
      <div className="summary-bar" style={{ height: '150px', overflowY: 'auto', border: '1px solid #ccc', padding: '10px' }}>
        <h3>Summary:</h3>
        <pre>{summary}</pre> {/* Use <pre> to maintain formatting for bullet points */}
      </div>
    </div>
  );
}

export default App;
